import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import { AntDesign } from '@expo/vector-icons'; 

const DiscussionDetails = (props) => {
  let data = props.route.params.discussion;
  console.log(data);
  return (
    <View style={styles.container}>
      <View style={styles.row}>
        <Text style={styles.title}>{data.title} "</Text><AntDesign name="user" size={24} color="black" /><Text>{data.author}</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
  },
  title: {
    fontSize: 36,
  },
  row:{
    flexDirection:'row'
  }
});

export default DiscussionDetails;
