import React, { useState } from 'react';
import { View, Text, StyleSheet, Image, TextInput } from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import { FlatList, TouchableOpacity } from 'react-native-gesture-handler';

const DiscussionDetails = (props) => {
  let data = props.route.params.discussion;

  const item = props.route.params.discussion;
  const baseURL = 'https://whatshotapp.herokuapp.com/api';
  const [comment, setComment] = useState('');
  const [user, setUser] = useState(props.route.params.username);

  const addComment = async () => {
    if (comment != '' && user != '') {
      const data = await fetch(baseURL + '/dis/comment/' + item._id, {
        method: 'put',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          comment: comment,
          commentAuthor: poster,
        }),
      });
      const discussions = await data.json();
      console.log(JSON.stringify(discussions));
    } else {
      Alert.alert('Add Comment', 'Please enetr comment for this article');
    }
  };
  console.log(data);
  return (
    <View style={styles.container}>
      <View style={styles.row}>
        <Text style={styles.title}>{data.title} </Text>
        <View>
          <Image style={styles.avatar} source={{ uri: data.authorAvatar }} />
        </View>
        <Text style={{ fontSize: 20, marginLeft: 5 }}>{data.author}</Text>
      </View>
      <Text></Text>
      <View
        style={{
          borderBottomColor: 'black',
          borderBottomWidth: 1,
          width: '100%',
        }}
      >
        <Text></Text>
      </View>
      <View>
        <Image style={styles.image} source={{ uri: data.postImage }} />
      </View>
      <View>
        <Text style={styles.content}>{data.content}</Text>
        <View
          style={{
            marginTop: 10,
            borderBottomColor: 'black',
            borderBottomWidth: 1,
            width: '100%',
          }}
        >
          <Text></Text>
        </View>
        <Text style={styles.comment}>Comments : </Text>

        {/* add comment */}
        <View style={styles.addComment}>
          <Text></Text>
          <TextInput placeholder="Enter comment here" />
          <TouchableOpacity style={styles.btnComment}>
            <Text
              style={{ fontSize: 18, fontWeight: 'bold', color: '#39C0F6' }}
            >
              Post
            </Text>
          </TouchableOpacity>
        </View>

        <FlatList
          data={data.comments}
          key={(item) => item._id}
          renderItem={({ item }) => (
            <View style={styles.comments}>
              <View style={{ flexDirection: 'row' }}>
                <Image
                  style={{ width: 47, height: 50 }}
                  source={{ uri: item.avatar }}
                />
                <Text style={{ fontSize: 18 }}>{item.commentAuthor}</Text>
              </View>

              <Text style={{ fontSize: 24 }}>{item.comment}</Text>
            </View>
          )}
        />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
  },
  title: {
    fontSize: 36,
    color: '#A251B0',
  },
  row: {
    marginTop: 40,
    flexDirection: 'row',
    margin: 10,
  },
  content: {
    margin: 20,
    marginTop: 30,
    fontSize: 28,
  },
  image: {
    marginTop: 20,
    width: 600,
    height: 400,
  },
  avatar: {
    padding: 5,
    width: 67,
    height: 70,
  },
  comment: {
    fontSize: 40,
    margin: 20,
  },
  comments: {
    margin: 20,
  },
  addComment: {
    flexDirection: 'row',
    margin: 20,
  },
  btnComment: {
    borderWidth: 1,
    borderRadius: 15,
    padding: 10,
    fontSize: 24,
  },
});

export default DiscussionDetails;
