import React, {useState,useEffect} from "react";
import { View, Text, StyleSheet, Image, ActivityIndicator, FlatList} from 'react-native';

const Discussions = props => {

  const baseURL = 'https://whatshotapp.herokuapp.com/api';
  const [allData, setAllData] = useState({});
  const [loading, setLoading] = useState(false);


  useEffect(() => {
    loadAllDiscussions();
  }, []);

  const loadAllDiscussions = async() => {
    setLoading(true);
    const data = await fetch(baseURL + '/dis/getAllDisccusions',{
      method: 'get'
    });
    const discussions = await data.json();
    setAllData(discussions);
    setLoading(false);
  }


    return(
        <View style={styles.container}>
          {
            loading ? (
              <ActivityIndicator color='#F7567C' size='large' />
            ) : (
              <View style={{width:'100%'}}>
              <FlatList
                data={allData.Disccusions}
                keyExtractor={item => item._id}
                renderItem={itemRow => 
                  <View style={styles.row}>
                    <Image source={{uri: itemRow.item.authorAvatar}} style={styles.avatar} />
                    <Text>{itemRow.item.title}</Text>
                  </View>}
            />
            </View>
            )
          }
        </View>
    )
}

const styles = StyleSheet.create({
  row:{
    width:'100%', backgroundColor:'#FCFCFC', flexDirection:'row',
    borderRadius:12, padding:12, marginBottom:12
  },
  avatar:{
    width:40, height:50
  },
  container: {
    flex: 1,
    backgroundColor: '#5D576B',
    padding:30
  },
  });
  
export default Discussions;
