import React from 'react';
import { View, Text, StyleSheet, Image } from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import { FlatList } from 'react-native-gesture-handler';

const DiscussionDetails = (props) => {
  let data = props.route.params.discussion;
  console.log(data);
  return (
    <View style={styles.container}>
      <View style={styles.row}>
        <Text style={styles.title}>{data.title} </Text>
        <View>
          <Image style={styles.avatar} source={{ uri: data.authorAvatar }} />
        </View>
        <Text style={{ fontSize: 20, marginLeft: 5 }}>{data.author}</Text>
      </View>
      <Text></Text>
      <View
        style={{
          borderBottomColor: 'black',
          borderBottomWidth: 1,
          width: '100%',
        }}
      >
        <Text></Text>
      </View>
      <View>
        <Image style={styles.image} source={{ uri: data.postImage }} />
      </View>
      <View>
        <Text style={styles.content}>{data.content}</Text>
        <View
          style={{
            marginTop: 10,
            borderBottomColor: 'black',
            borderBottomWidth: 1,
            width: '100%',
          }}
        >
          <Text></Text>
        </View>
        <Text style={styles.comment}>Comments : </Text>
        <FlatList
          data={data.comments}
          key={(item) => item._id}
          renderItem={({ item }) => (
            <View>
              <Text style={styles.comments}>{item.comment}</Text>
            </View>
          )}
        />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
  },
  title: {
    fontSize: 36,
    color: '#A251B0',
  },
  row: {
    marginTop: 40,
    flexDirection: 'row',
    margin: 10,
  },
  content: {
    marginTop: 30,
    fontSize: 28,
  },
  image: {
    marginTop: 20,
    width: 600,
    height: 400,
  },
  avatar: {
    padding: 5,
    width: 67,
    height: 70,
  },
  comment: {
    fontSize: 40,
    margin: 5,
  },
  comments:{
    fon
  }
});

export default DiscussionDetails;
